import React,{ useState } from "react";
import './signup.css'
import { useNavigate } from 'react-router-dom';
import { useForm } from "react-hook-form";


function Signup(){

    const navigate = useNavigate();

    const [email, setEmail] = useState("");
    const [ fullname , setName] = useState("");
    const [ phone , setPhone ] = useState("");
    const [ password , setPassword ] = useState("");
    const [confirmPassword, setConfirmPassword ] = useState("");

//====================== Validation ===========================

    const [ emailErr , setEmailErr ] = useState('');
    const [ passwordErr , setPasswordErr ] = useState('');
    const [ phoneErr , setPhoneErr ] = useState('');

  const handleSubmit = (e)=> {

      e.preventDefault();

      if (/^\w+([\.-]?\w+)*@\w+([\.-]?\w+)*(\.\w{2,3})+$/.test(email)){
        setEmailErr('');
          if(password.length < 6 && password === confirmPassword ){
              setPasswordErr("Password should atleast contain 6 characters and also equals to confirm password");
          }else{
            setPasswordErr('')
          }
      }else{
        setEmailErr("Invalid Email");
      }

  }

  return (
    <div className="totalBody">
    <div className="container">
      <div className="boxLogin">

        <form onSubmit={ handleSubmit }>
          <h1> Signup Form </h1>
        <div className="input">
        <input
            type="text"
            value={fullname}
            className="inputLogin"
            onChange={(e) => {
              setName(e.target.value);
            }}
            placeholder = "Enter your full name"
            name="email"
          />
          { emailErr && <p style={{ color : "red", fontSize : "1rem" }} >{ emailErr }</p> }
          <input
            type="email"
            value={email}
            className="inputLogin"
            onChange={(e) => {
              setEmail(e.target.value);
            }}
            placeholder = "Enter your email Address"
            name="email"
          />
          <input
            type="number"
            className="inputLogin"
            value={phone}
            onChange={(e) => {
              setPhone(e.target.value);
            }}
            placeholder="Enter your phone"
            name="phone"
          />
          { passwordErr && <p style={{ color : "red" , fontSize : "1rem" }} >{ passwordErr }</p> }
          <input
            type="password"
            className="inputLogin"
            value={password}
            onChange={(e) => {
              setPassword(e.target.value);
            }}
            placeholder="Enter the password"
            name="password"
          />
            <input
              type="password"
              className="inputLogin"
              value={confirmPassword}
              onChange={(e) => {
                setConfirmPassword(e.target.value);
              }}
              placeholder="Confirm the password"
              name="password"
            />

        </div>

        <div className="submit">
          <button type="submit" className="submitButton"> Sign Up </button>
          <p onClick={ ()=>{ navigate('/login') } } style = {{ fontSize : "0.8rem" , cursor:"pointer" }} >Already Have an Account ? Login Now</p>
        </div>
        </form>
      </div>
    </div>
    </div>
  );

}

export default Signup;
