import React,{ useState } from "react";
import './signup.css'
import { useNavigate } from 'react-router-dom';
import { useForm } from "react-hook-form";
const axios = require('axios');

const api = axios.create({ 
  baseURL : 'http://loa'
})

function Signup(){

    const navigate = useNavigate();

    // const [email, setEmail] = useState("");
    // const [ fullname , setName] = useState("");
    // const [ phone , setPhone ] = useState("");
    // const [ password , setPassword ] = useState("");
    // const [confirmPassword, setConfirmPassword ] = useState("");

//====================== Validation ===========================

    const { register, handleSubmit ,  formState: { errors } } = useForm();

    const onSubmit = (data)=>{

        console.log(data);


    }
    console.log(errors);
    console.log(errors.phone , errors.email , errors.password);
  return (
    <div className="totalBody">
    <div className="container">
      <div className="boxLogin">

        <form onSubmit={ handleSubmit(onSubmit) }>
          <h1> Signup Form </h1>
        <div className="input">
            { errors.fullName && <p style={{ color : "red" , fontSize:"0.8rem" }} >{errors.fullName.message}</p> }
        <input
            type="text"
            className="inputLogin"
            placeholder = "Enter your full name"
            name="email"
            { ...register("firstName",{ required : "Full name must be filled" }) }
          />

          { errors.email && <p style={{ color : "red" , fontSize:"0.8rem" }} >{errors.email.message}</p>  }

          <input
            type="email"
            className="inputLogin"
            placeholder = "Enter your email Address"
            name="email"
            { ...register("email",{ required : "Email must be filled" ,  pattern: {
                value: /^[A-Z0-9._%+-]+@[A-Z0-9.-]+\.[A-Z]{2,}$/i,
                message: "invalid email address"
              } }) }

          />

          { errors.phone && <p style={{ color : "red" , fontSize:"0.8rem" }} >{errors.phone.message}</p> }

          <input
            type="number"
            className="inputLogin"
            placeholder="Enter your phone"
            name="phone"
            { ...register("phone",{ required : "Phone is required" , minLength:  { value : 10 , message : "phone must have length of 10" }  , maxLength : { value : 10 , message : "phone must have length of 10" } }) }
          />

          { errors.password && <p style={{ color : "red" , fontSize:"0.8rem" }} >{errors.password.message}</p> }


          <input
            type="password"
            className="inputLogin"
            placeholder="Enter the password"
            name="password"
            { ...register("password",{ required : "Password is required" , minLength : { value : 6 , message : "password must contain atleast 6 characters" } }) }
          />

          { errors.confirmPassword && <p style={{ color : "red" , fontSize:"0.8rem" }} >{errors.confirmPassword.message}</p> }


            <input
              type="password"
              className="inputLogin"
              placeholder="Confirm the password"
              name="password"
              { ...register("confirmPassword",{ required : "Password is required" , minLength : { value : 6 , message : "password must contain atleast 6 characters" } }) }
            />

        </div>

        <div className="submit">
          <button type="submit" className="submitButton"> Sign Up </button>
          <p onClick={ ()=>{ navigate('/login') } } style = {{ fontSize : "0.8rem" , cursor:"pointer" }} >Already Have an Account ? Login Now</p>
        </div>
        </form>
      </div>
    </div>
    </div>
  );

}

export default Signup;
